import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";

const MyMenu = styled(NavLink)`
  color: #fff;
  text-transform: uppercase;
  margin-left: 25px;
  text-weight: bold;
  :hover {
    text-decoration: line-through;
    color: #fff;
  }
`;

const InThisMenu = props => (
  <MyMenu {...props} style={{textDecoration: "line-through"}}/>
);

const NotInThisMenu = styled(NavLink)`
  text-decoration: none;
`;

export class Menu extends Component {
  render() {
    return (
      <div>
        <MyMenu exact to="/" as={this.props==="/" && InThisMenu}>
          Home
        </MyMenu>
        <MyMenu exact to="/portfolio">
          Portfolio
        </MyMenu>
        <MyMenu exact to="/contact">
          Contact
        </MyMenu>
      </div>
    );
  }
}

export default Menu;
