import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";


const MyMenu = styled(NavLink)`
  color: #fff;
  text-transform: uppercase;
  margin-left: 25px;
  text-weight: bold;
  :hover {
    text-decoration: line-through;
    color: #fff;
  }
`;

export class Menu extends Component {
  state = {
    homeFlag: "false",
    PortFolioFlag: "false",
    ContactFlag: "false"
  };

  changeFlag = () => {
    if (this.props.pathname === "/") {
      this.setState({
        homeFlag: "true",
        PortFolioFlag: "false",
        ContactFlag: "false"
      });
    }
  };

  render() {
      this.changeFlag();
    return (
      <div>
        <MyMenu exact to="/" style={this.state.homeFlag && { textDecoration: "line-through" }}>
          Home
        </MyMenu>
        <MyMenu exact to="/portfolio">
          Portfolio
        </MyMenu>
        <MyMenu exact to="/contact">
          Contact
        </MyMenu>
      </div>
    );
  }
}

export default Menu;
