import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";

const MyMenu = styled(NavLink)`
  color: #fff;
  text-transform: uppercase;
  margin-left: 25px;
  text-weight: bold;
  :hover {
    text-decoration: line-through;
    color: #fff;
  }
`;

export class Menu extends Component {
  render() {
      return (
      <div>
        <MyMenu
          exact
          to="/"
          style={this.props.pathname=== "/" && { textDecoration: "line-through" }}
        >
          Home
        </MyMenu>
        <MyMenu exact to="/portfolio">
          Portfolio
        </MyMenu>
        <MyMenu exact to="/contact">
          Contact
        </MyMenu>
      </div>
    );
  }
}

export default Menu;
